package basicofjava;

public class EvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int num = 122;
        
        System.out.println("Even numbers between 121 and 229:");
        
        while (num <= 229) {
            System.out.print(num + " ");
            num +=2;
        }
    }

	}


